--
-- Structure de la table `message`
--

CREATE TABLE IF NOT EXISTS `message` (
  `cle` mediumint(8) unsigned NOT NULL AUTO_INCREMENT,
  `civilite` enum('Monsieur','Madame','Mademoiselle') NOT NULL DEFAULT 'Monsieur',
  `nom` varchar(125) NOT NULL,
  `prenom` varchar(125) NOT NULL,
  `email` varchar(255) NOT NULL,
  `web` varchar(255) NOT NULL,
  `tel` varchar(30) NOT NULL,
  `sujet` varchar(255) NOT NULL,
  `message` text NOT NULL,
  `connu` enum('TV Radio','Internet','Papier','Bouche à Oreille','') NOT NULL DEFAULT '',
  `Date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`cle`)
) ENGINE=MyISAM DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;

--
-- Contenu de la table `message`
--

