CREATE TABLE IF NOT EXISTS `article` (
  `cle` mediumint(8) unsigned NOT NULL AUTO_INCREMENT,
  `id` tinyint(3) unsigned NOT NULL,
  `page` enum('Accueil','Prestations','Références','A Propos','CV & LM','Contact','Actualité') NOT NULL DEFAULT 'Accueil',
  `title` tinytext NOT NULL,
  `keyword` tinytext NOT NULL,
  `description` text NOT NULL,
  `date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `categorie` varchar(100) NOT NULL,
  `genre` varchar(100) NOT NULL,
  `lecture` smallint(5) unsigned NOT NULL,
  `content` mediumtext NOT NULL,
  PRIMARY KEY (`cle`),
  UNIQUE KEY `id` (`id`),
  KEY `id_2` (`id`),
  KEY `page` (`page`),
  FULLTEXT KEY `title` (`title`,`keyword`,`description`,`categorie`,`genre`),
  FULLTEXT KEY `title_2` (`title`,`keyword`,`description`,`categorie`,`genre`,`content`)
) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=9 ;

